function functionName(config, jsparams, result) 
{
	let hh = {            "UserAgent": "Mozilla/5.0 (iPhone; CPU iPhone OS 13_2_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/13.0.3 Mobile/15E148 Safari/604.1",            "referer": "https://www.hztoon.com/"        };

	// 生成的内容需添加到response中再返回，因为result里面包含了分页请求等参数，如果无分页时可以直接返回的
	result.content = JSON.stringify({'urls':result.content, 'httpHeaders':hh});
	return result;}

