function functionName(config, params, result)
{

let res = JSON.parse(result.match(/serchArry=(.*?);/)[1]);//console.log(res);let list = [];//console.log(typeof(res));for (var i = 0; i < res.length; i++) {    let bookInfo = {};    last_chapter = res[i]['latestVolume']    bookInfo.detailUrl = config.host+"/comic/"+res[i]['id'];    bookInfo.bookName = res[i]['title'];    bookInfo.cover = config.host+"/"+res[i]['cover'];      bookInfo.author = res[i]['authorstr'];    bookInfo.lastChapterTitle =last_chapter['title'];    list.push(bookInfo);   //console.log(bookInfo)}

	return {'list':list};
}

