chapterList.JSParser:
function functionName(config, params, result)
{
	let res = JSON.parse(result.match(/,"data":(.*?\])\}\]\);/)[1]);
//console.log(res);
 let former_url =params.queryInfo.detailUrl.replace(/comic/g,"view");

let list = [];

for (var i = 0; i < res.length; i++) {
    let chapterInfo = {};
    chapterInfo.title = res[i]['chapter_name'];
    chapterInfo.url = former_url+"/" + res[i]['id'];

    // 传递更多参数给下一级
    chapterInfo.detailUrl = params.queryInfo.detailUrl;

    list.push(chapterInfo);

   //console.log(chapterInfo)
}
	return {'list':list};
}
