@js:

let imgUrls = result.split("|");

let tagUrl = imgUrls[0];
let imgs = ""
// console.log(imgUrls)
for(let i = 1;i<imgUrls.length;i++){

    if (i == imgUrls.length-1) {

        imgs += encodeURI(tagUrl+imgUrls[i]);
    }else{

        imgs += encodeURI(tagUrl+imgUrls[i])+"\n";
    }
}

return imgs;

