function functionName(config, params, result)
{
	if(!params.lastResponse){
		let url = result.match(/oScript.*?=\s+"(.*?)";/)[1];

		return {'more': true, 'success': true, 'autoRequestMore': true,'url':url};
	}


let res = result.match(/((http|https).*?jpg)/g);
let imgUrls = "";
for(var i =0;i<res.length;i++){
	    if (i == res.length-1) {

        imgUrls += encodeURI(res[i]);
    }else{

        imgUrls += encodeURI(res[i])+"\n";
    }
}


return JSON.stringify({'urls': imgUrls, 'httpHeaders':config.httpHeaders});
}

