chapterList.requestInfo:
@js:

if(result.indexOf("_")!=-1){

let res = result.split("_")
let url = config.host+"/book"+res[0]+'/'+res[1]

return {'url':url,'POST':false,'httpHeaders':config.httpHeaders,'cacheTime':3600};
}else{

return {'url':result,'POST':false,'httpHeaders':config.httpHeaders,'cacheTime':3600};

}

searchBook.requestInfo:
@js:

let url  = config.host.replace("www","m") +"/s.php";
let hp = {
    "keyword":params.keyWord,
    "t":"1"
}

let hh = {
    "User-Agent": " Mozilla/5.0 (iPhone; CPU iPhone OS 13_2_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/13.0.3 Mobile/15E148 Safari/604.1",
    "referer": " http://m.huanyue1234.com/"
}

return {'url':url,'POST':true,'httpHeaders':hh,'httpParams':hp,'cacheTime':3600}

