chapterList.JSParser:
function functionName(config, params, result) {
    let list = [];
    let xpath = params.nativeTool.XPathParserWithSource(result);
    let res = xpath.queryWithXPath("//a[contains(@class,\"hl-tabs-btn hl-slide-swiper\")]");
    let playSrcList = [];
    // 取出线路名称与索引
    res.forEach((x, i) => {
        let attr = x.attributes();
        let title = attr['alt'];
        playSrcList.push(title);
    });
    let sortItem = xpath.queryWithXPath("//ul[@id=\"hl-plays-list\"]");
    sortItem.forEach((x, i) => {
        let itemList = x.queryWithXPath("//li/a");
        itemList.forEach(a => {
            let attr = a.attributes();
            list.push({
                "title": playSrcList[i] + '- ' + a.content(),
                "url": attr.href,
            });
        });
    });
    return { 'list': list };
}

