chapterContent.content:
@js:
let url = String(result);
let url_index = url.indexOf('url=');
if (url_index != -1) {
    url = url.substring(url_index + 4);
}
url = url.replace('&jump=', '').replace('https://jx.anfuns.cn/?url=', '').replace(/&next=.*/g, '');

if (RegExp(/m3u8/g).test(url)) {
    config.httpHeaders['origin'] = 'https://jx.anfuns.cn';
}
return JSON.stringify({
    'url': url,
    'httpHeaders': config.httpHeaders,
});

