chapterContent.requestInfo:
@js:
if (params.lastResponse) {
    return params.lastResponse;
}
let url = "https://vip.qianyicp.com/vip_a/vip.php?url=" + encodeURIComponent(result);
let httpHeaders = {
    "Accept": "*/*",
    "Referer": "https://vip.jx4k.com/",
    "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/95.0.4638.54 Safari/537.36",
}
let cacheKey = new Date().getTime();

return {
    "cacheKey": String(cacheKey),
    "url": url,
    "httpHeaders": httpHeaders
};

