function functionName(config, jsparams, result) {
    let ts = result.updateTime.split("\n");
    let list = [];
    for (let i = 0; i < ts.length; i++) {
        if ("url" in result["list"][i]) {
            let title = result["list"][i]["title"];
            let titles = title.split("\n");
            let urls = result["list"][i]["url"].split("\n");
            for (let j = 0; j < titles.length; j++) {
                list.push({"title": ts[i] + "-" + titles[j], "url": urls[j]})
            }
        }
    }
    return {"updateTime": "", "list": list};}

