chapterList.JSParser:
function functionName(config, params, result) {

    params.nativeTool.logWithKey(params, 'params');
    let list = [{ 'title': '1', 'url': params.queryInfo.detailUrl }];
    result.list.filter(x => x.title != '下一页').forEach(x => {
        list.push(x);
    });

    return { 'list': list };
}
