chapterContent.content:
@js:

let res = result.match(/chapter_list_all:(.*)/)[1]
res = JSON.parse(res);

let urls = "";

for(let i = 0;i<res.length;i++){
	if(i<res.length-1){
		if(res[i].match(/url=(.*)/)){
		var img_url = res[i].match(/url=(.*)/)[1].replace(/\.low/g,".high");
		urls =urls+ encodeURI(img_url)+"\n";
		}else{
			var img_url = res[i]
			urls =urls+ encodeURI(img_url)+"\n";
		}

	}else{
		if(res[i].match(/url=(.*)/)){
		var img_url = res[i].match(/url=(.*)/)[1].replace(/\.low/g,".high");
		urls =urls+ encodeURI(img_url);
		}else{
			var img_url = res[i]
			urls =urls+ encodeURI(img_url);
		}

	}
}

return urls

