searchBook.JSParser:
function functionName(config, params, result)
{

//params.nativeTool.log(config)
let list = [];

result = result.match(/getsortlistNewCb\((.*?)\)/)[1];

result = JSON.parse(result);
// console.log(result);
let comic_lists = result['data']['page']['comic_list'];
// console.log(comic_list);
for (var i = 0; i < comic_lists.length; i++) {

	let bookInfo = {};
	bookInfo.detailUrl = config.host+"/manhua/"+comic_lists[i]['comic_id']+"/";
	bookInfo.bookName = comic_lists[i]['comic_name'];

	bookInfo.lastChapterTitle = comic_lists[i]['last_chapter']['name'];
	list.push(bookInfo);
}
// console.log(list);
return {'list':list};
}

