chapterList.JSParser:
function functionName(config, params, result)
{
	let list = [];
	res =result.reverse();
	url = config.host+"/"+params.queryInfo.detailUrl+"/";
	for (var i = 0; i<res.length; i++) {
		let chapterInfo = {};
		chapterInfo.title = res[i]['chaptername'];
		chapterInfo.url = url+res[i]['chapterid']+".html";
		list.push(chapterInfo);

		//console.log(chapterInfo.title,chapterInfo.url);


	}

	return {'list':list};
}

