@js:

let res = result.match(/chapterImages\s+= (\[.*?);var\s+chapterPath\s=\s"(.*?)";var/);
let imgs = JSON.parse(res[1]);
let urlPath = "https://res.wangjiabao.top/"+res[2];
console.log(imgs);
let urls = "";
for (var i = 0 ; i < imgs.length; i++) {
   if (imgs[i].indexOf("http")!=-1) {

        let url = imgs[i];
        urls = urls +url +"\n"; 

  }
    else{
        let url = urlPath +imgs[i];
        urls = urls +url +"\n";

    }

}

return JSON.stringify({'urls': urls.split('\n'), 'httpHeaders': {            "UserAgent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/72.0.3626.121 Safari/537.36",            "referer":config.host +params.queryInfo.url        }});
