shupingHome.detail:
@js:
if(params.filter=="1"){
return result.info;
}else if(params.filter=="2"){
	return '评分：'+result.book.score+'\n用户评分：'+result.score+ '\n用户评论：'+result.content+'\n评论时间：'+result.create_time;
}

shupingHome.url:
@js:
if(params.filter=="1"){
	return '/api/getThreadDetail?thread_id=' + result.thread_id;
}else if(params.filter=="2"){
	return result.score_id;
}

shupingHome.requestInfo:
@js:
let filter = params.filter;
let url = "";
if(filter=="1"){
	url = "/api/listThread?";
}else if(filter =="2"){
	url = "/api/appHomePage?";
}
url += "type=all&page=$page&pageSize=10";
url = url.replace('$page',params.pageIndex);
return {
	"url":url,
	"cacheTime":10
};

shupingHome.title:
@js:
if(params.filter=="1"){
return result.title;
}else if(params.filter=="2"){
	return '书籍：'+'《'+result.book.title+'》';
}
