function functionName(config, params, result) {    let list = [];    if (params.lastResponse == undefined) {        let res = JSON.parse(result);        let url = res.data.resources[0].chapterPageUrl;        return {            'url': url,            'httpHeaders': config.httpHeaders,            'more': true,            'success': true,            'autoRequestMore': true,            'runIndex': 1,        };    }    let url = params.lastResponse.url;    let host = url.match(/\w+:\/\/[a-zA-Z0-9][-a-zA-Z0-9]{0,62}(\.[a-zA-Z0-9][-a-zA-Z0-9]{0,62})+\.?/)[0];    let contentRule;    let ruleString;    if (RegExp("www.xbiqupao.com").test(url) ||        RegExp("www.xbiquge.la").test(url) ||        RegExp("www.xbiquwx.la").test(url) ||        RegExp("www.zwwx.com").test(url) ||        RegExp("www.33yq.org").test(url) ||        RegExp("www.43kanshu.com").test(url) ||        RegExp("www.ddyueshu.com").test(url) ||        RegExp("www.mayiwxw.com").test(url) ||        RegExp("www.31xs.net").test(url) ||        RegExp("www.miaojiang8.net").test(url) ||        RegExp("www.shuhaiju.com").test(url) ||        RegExp("www.ranwen.la").test(url) ||        RegExp("www.7kzw.org").test(url) ||        RegExp("www.bqxs520.com").test(url) ||        RegExp("www.mibaoge.com").test(url) ||        RegExp("www.sobiquge.com").test(url) ||        RegExp("www.42biquge.com").test(url) ||        RegExp("www.tushumi.com").test(url)    ) {        ruleString = "//div[@id=\"list\"][last()]/dl/dd/a";        contentRule = "//div[@id=\"content\"]//text()";    } else if (
         RegExp("www.biquw.com").test(url) ||
         RegExp("www.38dushu.com").test(url) 
) {           ruleString = "//div[@class=\"book_list\"]/ul/li/a";        contentRule = "//div[@id=\"htmlContent\"]//text()";    } else if (        RegExp("www.51kanshu.cc").test(url) ||        RegExp("www.tangsanshu.com").test(url) ||        RegExp("www.piaotian5.net").test(url) ||        RegExp("www.tycqge.com").test(url)    ) {        ruleString = "//div[@class=\"listmain\"][last()]/dl/dd/a";        contentRule = "//div[@id=\"content\"]//text()";    } else if (RegExp("www.soshuwu.com").test(url)) {        ruleString = "//div[@class=\"novel_list\"][last()]/dl/dd/a";        contentRule = "//div[@class=\"content\"]//text()";    } else if (RegExp("www.soeo.net").test(url)) {        ruleString = "//div[@id=\"content_1\"][last()]/a";        contentRule = "//div[@id=\"content\"]//text()";    } else if (RegExp("www.huanxiangji.com").test(url)) {        ruleString = "//ul[@id=\"section-list\"][last()]/li/a";        contentRule = "//div[@id=\"content\"]//text()";    } else if (RegExp("www.tszw.org").test(url)) {        ruleString = "//ul[@id=\"chapterlist\"]/li/a";        contentRule = "//div[@id=\"book_text\"]//text()";    } else if (RegExp("www.boquku.com").test(url)) {        ruleString = "//ul[@id=\"chapters-list\"]/li/a";        contentRule = "//div[@id=\"txtContent\"]//text()";    } else if (RegExp("www.yueduwu.com").test(url)) {        ruleString = "//ul[@class=\"chapters\"]/li/a";        contentRule = "//div[@id=\"acontent\"]/text()";    } else if (RegExp("www.trxs.org").test(url)) {        ruleString = "//ul[@class=\"clearfix\"]/li/a";        contentRule = "//div[@class=\"read_chapterDetail\"]//text()";    } else if (RegExp("www.duanqingsi.com").test(url)) {        ruleString = "//div[@class=\"chapterCon\"]/ul/li/a";        contentRule = "//div[@class=\"read_chapterDetail\"]//text()";    }    if (ruleString != undefined) {        let xpath = params.nativeTool.XPathParserWithSource(result);        let res = xpath.queryWithXPath(ruleString);        res.forEach((x, i) => {            let attrs = x.attributes();            let u = String(attrs.href);            list.push({                'title': x.content(),                'url': u.startsWith('/') ? host + u : u.startsWith('http') ? u : url + u,                'contentRule': contentRule,            });        })    }    return { 'list': list };}

