chapterContent.JSParser:
function functionName(config, params, result) {
    let content = '';
    let xpath = params.nativeTool.XPathParserWithSource(result);
    let res = xpath.queryWithXPath(params.queryInfo.contentRule);
    res.forEach(x => {
        content += x.content() + '\n';
    })
    return { 'content': content };
}

