chapterList.JSParser:
function functionName(config, params, result) {
    let list = [];
    let xpath = params.nativeTool.XPathParserWithSource(result);
    let res = xpath.queryWithXPath("//div[contains(@class,\"module-tab-item tab-item\")]");
    let playSrcList = [];
    // 取出线路名称与索引
    res.forEach((x, i) => {
        let attr = x.attributes();
        let title = attr['data-dropdown-value'];
        playSrcList.push(title);
    });
    let sortItem = xpath.queryWithXPath("//div[@class=\"sort-item\"]");
    sortItem.forEach((x, i) => {
        let itemList = x.queryWithXPath("//a");
        itemList.forEach(a => {
            let attr = a.attributes();
            list.push({
                "title": playSrcList[i] + '- ' + a.content(),
                "url": attr.href,
            });
        });
    });
    return { 'list': list };
}

