chapterList.JSParser:
function functionName(config, params, result) {
    let list = [];
    let xpath = params.nativeTool.XPathParserWithSource(result);
    let res = xpath.queryWithXPath("//*[@class=\"nav nav-tabs active\"]/li/a");
    res.forEach((x) => {
        let sadas = x.attributes();
        if (String(sadas.href).indexOf('#playlist') != -1) {
            let playlist = xpath.queryWithXPath("//*[@id='" + sadas.href.substring(1) + "']/ul/li/a");
            params.nativeTool.logWithKey(playlist, 'playlist');

            playlist.forEach((pl) => {
                let attr = pl.attributes();
                list.push({
                    "title": '线路:' + x.content() + '- ' + pl.content(),
                    "url": String(attr.href).startsWith('/') ? config.host + attr.href : attr.href,
                });
            });
        }
    });
    return { 'list': list };
}

