chapterContent.content:
@js:
let url = String(result);
let url_index = url.indexOf('url=');
if (url_index != -1) {
    url = url.substring(url_index + 4);
}
let id = url.lastIndexOf('http');
if (id > 4) {
    url = url.substring(id);
}
return url;

